package collection;import java.util.Iterator;//************************************************************************//  CollectionADT.java////  Defines the interface to an ordered  collection.//  Only Comparable elements are stored, kept in the order determined by//  the inherent relationship among the elements.//************************************************************************public interface CollectionADT<T> extends Iterable<T> {  //  Adds the specified element to this collection at the proper location  public boolean add(T element);  //  Removes the specified element from this collection.  //---->  Returns the element removed  public T remove(T element);  //  Removes and all elements from this collection.  public void clear();  //  Returns true if this collection contains the specified target element.  public boolean contains(T target);  //  Returns true if this collection contains no elements.  public boolean isEmpty();  //  Returns the number of elements in this collection.  public int size();  //  Returns an iterator for the elements in this collection.  public Iterator<T> iterator();  //  Returns a string representation of this collection.  public String toString();  //  Returns an array containing all of the elements in this collection.  public Object[] toArray();}