package collection;import collection.exceptions.EmptyCollectionException;//--------------------------------------------------------------------//  QueueInterface.java////  Defines the interface to a queue collection.//--------------------------------------------------------------------public interface QueueInterface<T> {  //  Adds one element to the rear of this queue.  public void enqueue(T element);  //  Removes and returns the element at the front of this queue.  public T dequeue() throws EmptyCollectionException;  //  Removes all elements in the queue.  public void dequeueAll();  //  Returns without removing the element at the front of this queue.  public T peek() throws EmptyCollectionException;  //  Returns true if this queue contains no elements.  public boolean isEmpty();  //  Returns the number of elements in this queue.  public int size();}